mapper.select:
select count(*)
from board
where status = 'Y' and board_genre = #{i}

select count(*) from board join recipe using(board_no) where status = 'Y'
and nation = #{nation}
and difficulty = #{difficulty}
and title like '%'||#{searchWord}||'%'

select * from board join recipe using(board_no) where status = 'Y' and board_genre = '레시피'
and nation = #{nation}
and difficulty = #{difficulty}
and title like '%'||#{searchWord}||'%'

select * from recipe
where board_no = #{bId}
order by recipe_no

select * from image
where recipe_no = #{rId}
where title_img = 0

mapper.update:
update board
set board_count = board_count + 1
where board_no = #{bId}

update board
set title = #{title}, content = #{content}
where board_no = #{boardNo}

update recipe
set intro = #{intro}, nation = #{nation}, difficulty = #{difficulty}, ingredients = #{ingredients}
where board_no = #{boardNo}

update board
set status = 'N'
where board_no = #{bId}
